package Lec_4;

public class Pre_de_in {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 9;
		a++;
		System.out.println(a);
		
		System.out.println(a++);// post increment
		
		System.out.println(++a);// pre increment
		int b=6;
		b--;
		System.out.println(b);
		
		System.out.println(b--);
		System.out.println(--b);
	}

}
